mapper.update:
update topic 
set 
	topic_name=#{topic_name}, 
	topic_explain=#{topic_explain}
where 
	topic_no = #{topic_no}

mapper.select:
select * from 
(
    select distinct member_no from topic_member where team_no = #{team_no}
    minus
    select distinct member_no from topic_member where topic_no = #{topic_no}
)A 
inner join member on A.member_no = member.member_no
